package org.leetcode;

public class FindTheDifference {

    public static void main(String[] args) {
        String s = "";
        String t = "";
        System.out.println(findTheDifference(s, t));
    }
    public static char findTheDifference(String s, String t) {
        if (s.isEmpty()) {
            return t.charAt(0);
        }
        char[] sChars = s.toCharArray();
        char[] tChars = t.toCharArray();
        char addedChar = 'a';

        int j = 0;
        int i = 0;
        int indexOfNotFound = -1;
        while(i < tChars.length) {
            int k = 0;
            while (k < sChars.length && tChars[i] != sChars[k]) {
                if (tChars[i] == sChars[k]) {
                    j++;
                } else if (k == sChars.length - 1) {
                    indexOfNotFound = i;
                    break;
                }
                k++;
            }
            if (indexOfNotFound != -1) {
                break;
            }
            i++;
        }
        if (indexOfNotFound != -1) {
            addedChar = tChars[indexOfNotFound];
        }
        return addedChar;
    }
}
